import React from "react";

function UniversalInput({ item }) {
  if (typeof item === "text") {
    return "text";
  } else if (typeof item === "number") {
    return "number";
  } else if (item.includes("@")) {
    return "email";
  } else {
    return "range";
  }
}

export default UniversalInput;
