import React, { Component } from "react";

export default class ClassCounterBoom extends Component {
  constructor(props) {
    super(props);
    this.state = { counting: 5 };
  }

  countingDown = () => {
    let { counting } = this.state;
    counting = counting - 1;
    this.setState({ counting });
  };

  render() {
    return (
      <div>
        <h1>{this.state.counting}</h1>{" "}
      </div>
    );
  }
}
