import React from "react";
const inputType = 100;
function UniversalInput({ inputType }) {
  console.log(inputType);
  if (inputType === "text") {
    return "text";
  } else if (inputType === "number") {
    return "number";
  } else if (inputType === "email") {
    return "email";
  } else {
    return "range";
  }
}

export default UniversalInput;
