import React, { Component } from "react";

class ClassCounterZwiekszanieLimituLicznika extends Component {
  constructor(props) {
    super(props);
    this.state = { ticks: 0 };
  }
  incrementThicks = () => {
    let { ticks } = this.state;
    if (ticks < 10) {
      ticks = ticks + 1;
      this.setState({ ticks });
      let item = (
        <button onClick={this.incrementThicks}>
          Lekcja 4 Zad 4 : {this.state.ticks}
        </button>
      );
    } else {
      let item = "przekroczyłeś";
    }
  };

  render() {
    return <div>{item}</div>;
  }
}

export default ClassCounterZwiekszanieLimituLicznika;
