import React, { Component } from "react";

class ClassCounterZwiekszanieLimituLicznika extends Component {
  constructor(props) {
    super(props);
    this.state = { ticks: 0 };
  }
  incrementThicks = () => {
    let visibl;
    let { ticks } = this.state;
    if (ticks < 10) {
      let visibl = true;
      ticks = ticks + 1;
      this.setState({ ticks });
    } else visibl = false;
  };

  render() {
    return (
      <div>
        <button onClick={this.incrementThicks} disabled={this.visibl}>
          Lekcja 4 Zad 4 : {this.state.ticks}
        </button>
      </div>
    );
  }
}

export default ClassCounterZwiekszanieLimituLicznika;
