import React from "react";
const item = 5;
function UniversalInput({ item }) {
  if (typeof item === "text") {
    return "text";
  } else if (typeof item === "number") {
    return "number";
  } else if (typeof item === "email") {
    return "email";
  } else {
    return "range";
  }
}

export default UniversalInput;
